select
	member.Id member_id,
	member.SpvId member_spvId,
	member.EmailRegisterValidId member_emailRegisterValidId,
	member.Type member_type,
	member.Code member_code,
	member.Account member_account,
	member.PhoneAccount member_phoneAccount,
	member.EmailAccount member_emailAccount,
	member.Password member_password,
	member.UmpayUserNo member_umpayUserNo,
	member.UmpayAccountNo member_umpayAccountNo,
	member.PayPassword member_payPassword,
	member.Nickname member_nickname,
	member.Avatar member_avatar,
	member.ValidEmail member_validEmail,
	member.MobilePhone member_mobilePhone,
	member.OfficePhone member_officePhone,
	member.CertificateType member_certificateType,
	member.CertificateNo member_certificateNo,
	member.Name member_name,
	member.Male member_male,
	member.Birthday member_birthday,
	member.FamilyRole member_familyRole,
	member.Rrac member_rrac,
	member.RiskScore member_riskScore,
	member.ProvinceSpell member_provinceSpell,
	member.ProvinceCode member_provinceCode,
	member.CityCode member_cityCode,
	member.CountryCode member_countryCode,
	member.ProvinceName member_provinceName,
	member.CityName member_cityName,
	member.CountryName member_countryName,
	member.Address member_address,
	member.Income member_income,
	member.Job member_job,
	member.Education member_education,
	member.ChildrenCount member_childrenCount,
	member.IsFreePassword member_isFreePassword,
	member.IsEmailValid member_isEmailValid,
	member.IsRealname member_isRealname,
	member.IsActivate member_isActivate,
	member.IsOpenAccount member_isOpenAccount,
	member.IsBindingCard member_isBindingCard,
	member.IsSignature member_isSignature,
	member.SignatureTime member_signatureTime,
	member.OpenAccountTime member_openAccountTime,
	member.RegisterTime member_registerTime,
	member.ModifyTime member_modifyTime,
	member.SerialNo member_serialNo,
	member.ResetTime member_resetTime,
	member.ReferralCode member_referralCode,
	member.RecommendIdcode member_recommendIdcode,	
	emailActivateValid.Id emailActivateValid_id,
	emailActivateValid.BussinessCode emailActivateValid_bussinessCode,
	emailActivateValid.EmailAccount emailActivateValid_emailAccount,
	emailActivateValid.Password emailActivateValid_password,
	emailActivateValid.SerialNo emailActivateValid_serialNo,
	emailActivateValid.Nickname emailActivateValid_nickname,
	emailActivateValid.ExpiredTime emailActivateValid_expiredTime,
	emailActivateValid.RegisterTime emailActivateValid_registerTime,
	spvCorporation.Id spvCorporation_id,
	spvCorporation.Type spvCorporation_type,
	spvCorporation.Code spvCorporation_code,
	spvCorporation.UmpayAccountNo spvCorporation_umpayAccountNo,
	spvCorporation.AvailableAmount spvCorporation_availableAmount,
	spvCorporation.FrozenAmount spvCorporation_frozenAmount,
	spvCorporation.UmpayMerchantNo spvCorporation_umpayMerchantNo,
	spvCorporation.Name spvCorporation_name,
	spvCorporation.ShortName spvCorporation_shortName,
	spvCorporation.BankName spvCorporation_bankName,
	spvCorporation.BankCode spvCorporation_bankCode,
	spvCorporation.BankCardNumber spvCorporation_bankCardNumber,
	spvCorporation.SignaturePicPath spvCorporation_signaturePicPath,
	spvCorporation.Phone spvCorporation_phone,
	spvCorporation.Fax spvCorporation_fax,
	spvCorporation.PostCode spvCorporation_postCode,
	spvCorporation.Address spvCorporation_address,
	spvCorporation.Description spvCorporation_description,
	spvCorporation.Status spvCorporation_status,
	spvCorporation.CreateTime spvCorporation_createTime,
	spvCorporation.ModifyTime spvCorporation_modifyTime
from CC_Member member
inner join CC_EmailActivateValid emailActivateValid on emailActivateValid.Id=member.EmailRegisterValidId
inner join CC_SpvCorporation spvCorporation on spvCorporation.Id=member.SpvId


		and Id=${id}
		and SpvId=${spvId}
		and EmailRegisterValidId=${emailRegisterValidId}
		and Type=${type}
		and Code=#{code}
		and Account=#{account}
		and PhoneAccount=#{phoneAccount}
		and EmailAccount=#{emailAccount}
		and Password=#{password}
		and UmpayUserNo=#{umpayUserNo}
		and UmpayAccountNo=#{umpayAccountNo}
		and PayPassword=#{payPassword}
		and Nickname=#{nickname}
		and Avatar=#{avatar}
		and ValidEmail=#{validEmail}
		and MobilePhone=#{mobilePhone}
		and OfficePhone=#{officePhone}
		and CertificateType=${certificateType}
		and CertificateNo=#{certificateNo}
		and Name=#{name}
		and Male=${male}
		and Birthday=#{birthday}
		and FamilyRole=${familyRole}
		and Rrac=${rrac}
		and RiskScore=${riskScore}
		and ProvinceSpell=#{provinceSpell}
		and ProvinceCode=${provinceCode}
		and CityCode=${cityCode}
		and CountryCode=${countryCode}
		and ProvinceName=#{provinceName}
		and CityName=#{cityName}
		and CountryName=#{countryName}
		and Address=#{address}
		and Income=${income}
		and Job=${job}
		and Education=${education}
		and ChildrenCount=${childrenCount}
		and IsFreePassword=${isFreePassword}
		and IsEmailValid=${isEmailValid}
		and IsRealname=${isRealname}
		and IsActivate=${isActivate}
		and IsOpenAccount=${isOpenAccount}
		and IsBindingCard=${isBindingCard}
		and IsSignature=${isSignature}
		and SignatureTime=#{signatureTime}
		and OpenAccountTime=#{openAccountTime}
		and RegisterTime=#{registerTime}
		and ModifyTime=#{modifyTime}
		and SerialNo=#{serialNo}
		and ResetTime=${resetTime}
		and ReferralCode=#{referralCode}
		and RecommendIdcode=#{recommendIdcode}

