mapper.insert:
		insert into CC_Member

			,Id
			,SpvId
			,EmailRegisterValidId
			,Type
			,Code
			,Account
			,PhoneAccount
			,EmailAccount
			,Password
			,UmpayUserNo
			,UmpayAccountNo
			,PayPassword
			,Nickname
			,Avatar
			,ValidEmail
			,MobilePhone
			,OfficePhone
			,CertificateType
			,CertificateNo
			,Name
			,Male
			,Birthday
			,FamilyRole
			,Rrac
			,RiskScore
			,ProvinceSpell
			,ProvinceCode
			,CityCode
			,CountryCode
			,ProvinceName
			,CityName
			,CountryName
			,Address
			,Income
			,Job
			,Education
			,ChildrenCount
			,IsFreePassword
			,IsEmailValid
			,IsRealname
			,IsActivate
			,IsOpenAccount
			,IsBindingCard
			,IsSignature
                ,SignatureTime
                ,OpenAccountTime
                ,RegisterTime
                ,ModifyTime
			,SerialNo
			,ResetTime
			,ReferralCode
			,RecommendIdcode

		values

			,${id}
			,${spvId}
			,${emailRegisterValidId}
			,${type}
			,#{code}
			,#{account}
			,#{phoneAccount}
			,#{emailAccount}
			,#{password}
			,#{umpayUserNo}
			,#{umpayAccountNo}
			,#{payPassword}
			,#{nickname}
			,#{avatar}
			,#{validEmail}
			,#{mobilePhone}
			,#{officePhone}
			,${certificateType}
			,#{certificateNo}
			,#{name}
			,${male}
			,#{birthday}
			,${familyRole}
			,${rrac}
			,${riskScore}
			,#{provinceSpell}
			,${provinceCode}
			,${cityCode}
			,${countryCode}
			,#{provinceName}
			,#{cityName}
			,#{countryName}
			,#{address}
			,${income}
			,${job}
			,${education}
			,${childrenCount}
			,${isFreePassword}
			,${isEmailValid}
			,${isRealname}
			,${isActivate}
			,${isOpenAccount}
			,${isBindingCard}
			,${isSignature}
            	,#{signatureTime}
            	,#{openAccountTime}
            	,#{registerTime}
            	,#{modifyTime}
			,#{serialNo}
			,${resetTime}
			,#{referralCode}
			,#{recommendIdcode}


			  select LAST_INSERT_ID() as generatedKey

