mapper.insert:
		insert into CC_SpvCorporation

			,Id
			,Type
			,Code
			,UmpayAccountNo
			,AvailableAmount
			,FrozenAmount
			,UmpayMerchantNo
			,Name
			,ShortName
			,BankName
			,BankCode
			,BankCardNumber
			,SignaturePicPath
			,Phone
			,Fax
			,PostCode
			,Address
			,Description
			,Status
                ,CreateTime
                ,ModifyTime

		values

			,${id}
			,${type}
			,#{code}
			,#{umpayAccountNo}
			,${availableAmount}
			,${frozenAmount}
			,#{umpayMerchantNo}
			,#{name}
			,#{shortName}
			,#{bankName}
			,#{bankCode}
			,#{bankCardNumber}
			,#{signaturePicPath}
			,#{phone}
			,#{fax}
			,#{postCode}
			,#{address}
			,#{description}
			,${status}
                ,now()
            	,#{modifyTime}


			  select LAST_INSERT_ID() as generatedKey

