mapper.insert:
		insert into EB_BudgetBasicInfo

			,Id
			,MemberId
			,EduGoalId
			,SerialNumber
			,Name
			,CostLevel
			,Age
			,Birthday
			,ProvinceSpelling
			,ProvinceCode
			,ProvinceName
			,CityCode
			,CityName
			,CountryCode
			,CountryName
			,AvatarPicture
			,BudgetCost
			,FinancingAtisfiedRate
			,Balance
			,IsLacking
			,Reserves
			,MonthlyInvestment
			,BirthYear
			,Sex
                ,CreateDate
                ,ModifyDate
			,IsValid
			,CreatorId

		values

			,${id}
			,${memberId}
			,${eduGoalId}
			,#{serialNumber}
			,#{name}
			,${costLevel}
			,${age}
			,#{birthday}
			,#{provinceSpelling}
			,#{provinceCode}
			,#{provinceName}
			,#{cityCode}
			,#{cityName}
			,#{countryCode}
			,#{countryName}
			,#{avatarPicture}
			,${budgetCost}
			,#{financingAtisfiedRate}
			,${balance}
			,${isLacking}
			,${reserves}
			,${monthlyInvestment}
			,${birthYear}
			,${sex}
                ,now()
            	,#{modifyDate}
			,${isValid}
			,${creatorId}


			  select LAST_INSERT_ID() as generatedKey

