	,${id}
	,${itemDataId}
	,${areaId}
	,${eduGoalId}
	,${growStagesId}
	,${budgetCategoryId}
	,${budgetItemId}
	,${itemLowAmount}
	,${itemHighAmount}
	,${lowAmount}
	,${highAmount}
	,${amount}
	,${maxAmount}
	,${yearCount}
	,${years}
	,${maxYearCount}
	,${paymentAge}
	,${isGrading}
