mapper.insert:
		insert into PC_Product

			,Id
			,BasicProductId
			,SpvId
			,CategoryId
			,AssetCategoryCode
			,Name
			,Code
			,TotalAmount
			,SurplusAmount
                ,FirstPublishTime
			,UmpayAccountNo
			,Description
			,Status
			,DistributeStatus
			,CreatorId
                ,CreateTime
			,ModifierId
                ,ModifyTime
			,IsDelete
			,Sort
			,ViewName

		values

			,${id}
			,${basicProductId}
			,${spvId}
			,${categoryId}
			,#{assetCategoryCode}
			,#{name}
			,#{code}
			,${totalAmount}
			,${surplusAmount}
            	,#{firstPublishTime}
			,#{umpayAccountNo}
			,#{description}
			,${status}
			,${distributeStatus}
			,${creatorId}
                ,now()
			,${modifierId}
            	,#{modifyTime}
			,${isDelete}
			,${sort}
			,#{viewName}


			  select LAST_INSERT_ID() as generatedKey

