mapper.select:
select
	platformServiceFee.Id platformServiceFee_id,
	platformServiceFee.PlatformId platformServiceFee_platformId,
	platformServiceFee.SpvId platformServiceFee_spvId,
	platformServiceFee.ProductId platformServiceFee_productId,
	platformServiceFee.ReturnMoneyId platformServiceFee_returnMoneyId,
	platformServiceFee.SpvLoanId platformServiceFee_spvLoanId,
	platformServiceFee.TradeNumber platformServiceFee_tradeNumber,
	platformServiceFee.PayPlantformNumber platformServiceFee_payPlantformNumber,
	platformServiceFee.PrincipalAmount platformServiceFee_principalAmount,
	platformServiceFee.ServiceFee platformServiceFee_serviceFee,
	platformServiceFee.ServiceFeeRatio platformServiceFee_serviceFeeRatio,
	platformServiceFee.TakeTime platformServiceFee_takeTime,
	platformServiceFee.TakeStatus platformServiceFee_takeStatus,
	platformServiceFee.TakeFinishTime platformServiceFee_takeFinishTime,
	platformServiceFee.CheckingTime platformServiceFee_checkingTime,
	platformServiceFee.CheckingStatus platformServiceFee_checkingStatus,
	loan.Id spvCorporation_id,
	loan.SpvId spvCorporation_spvId,
	loan.ProductId spvCorporation_productId,
	loan.TradeNumber spvCorporation_tradeNumber,
	loan.PayPlantformNumber spvCorporation_payPlantformNumber,
	loan.ProductName spvCorporation_productName,
	loan.ProductCode spvCorporation_productCode,
	loan.TotalLoanAmount spvCorporation_totalLoanAmount,
	loan.SpvServiceFee spvCorporation_spvServiceFee,
	loan.SpvServiceFeeRatio spvCorporation_spvServiceFeeRatio,
	loan.LoanTime spvCorporation_loanTime,
	loan.LoanStatus spvCorporation_loanStatus,
	loan.CheckingTime spvCorporation_checkingTime,
	loan.CheckingStatus spvCorporation_checkingStatus,
	loan.Remark spvCorporation_remark
from TC_PlatformServiceFee platformServiceFee
inner join TC_Loan loan on loan.Id=platformServiceFee.SpvLoanId


		and Id=${id}
		and PlatformId=${platformId}
		and SpvId=${spvId}
		and ProductId=${productId}
		and ReturnMoneyId=${returnMoneyId}
		and SpvLoanId=${spvLoanId}
		and TradeNumber=#{tradeNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and PrincipalAmount=${principalAmount}
		and ServiceFee=${serviceFee}
		and ServiceFeeRatio=${serviceFeeRatio}
		and TakeTime=#{takeTime}
		and TakeStatus=${takeStatus}
		and TakeFinishTime=#{takeFinishTime}
		and CheckingTime=#{checkingTime}
		and CheckingStatus=${checkingStatus}
		and Remark=#{remark}

select 
	Id platformServiceFee_id,
	PlatformId platformServiceFee_platformId,
	SpvId platformServiceFee_spvId,
	ProductId platformServiceFee_productId,
	ReturnMoneyId platformServiceFee_returnMoneyId,
	SpvLoanId platformServiceFee_spvLoanId,
	TradeNumber platformServiceFee_tradeNumber,
	PayPlantformNumber platformServiceFee_payPlantformNumber,
	PrincipalAmount platformServiceFee_principalAmount,
	ServiceFee platformServiceFee_serviceFee,
	ServiceFeeRatio platformServiceFee_serviceFeeRatio,
	TakeTime platformServiceFee_takeTime,
	TakeStatus platformServiceFee_takeStatus,
	TakeFinishTime platformServiceFee_takeFinishTime,
	CheckingTime platformServiceFee_checkingTime,
	CheckingStatus platformServiceFee_checkingStatus,
	Remark platformServiceFee_remark
from TC_PlatformServiceFee


		and Id=${id}
		and PlatformId=${platformId}
		and SpvId=${spvId}
		and ProductId=${productId}
		and ReturnMoneyId=${returnMoneyId}
		and SpvLoanId=${spvLoanId}
		and TradeNumber=#{tradeNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and PrincipalAmount=${principalAmount}
		and ServiceFee=${serviceFee}
		and ServiceFeeRatio=${serviceFeeRatio}
		and TakeTime=#{takeTime}
		and TakeStatus=${takeStatus}
		and TakeFinishTime=#{takeFinishTime}
		and CheckingTime=#{checkingTime}
		and CheckingStatus=${checkingStatus}
		and Remark=#{remark}

select count(*) from TC_PlatformServiceFee


		and Id=${id}
		and PlatformId=${platformId}
		and SpvId=${spvId}
		and ProductId=${productId}
		and ReturnMoneyId=${returnMoneyId}
		and SpvLoanId=${spvLoanId}
		and TradeNumber=#{tradeNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and PrincipalAmount=${principalAmount}
		and ServiceFee=${serviceFee}
		and ServiceFeeRatio=${serviceFeeRatio}
		and TakeTime=#{takeTime}
		and TakeStatus=${takeStatus}
		and TakeFinishTime=#{takeFinishTime}
		and CheckingTime=#{checkingTime}
		and CheckingStatus=${checkingStatus}
		and Remark=#{remark}

mapper.insert:
		insert into TC_PlatformServiceFee

			,Id
			,PlatformId
			,SpvId
			,ProductId
			,ReturnMoneyId
			,SpvLoanId
			,TradeNumber
			,PayPlantformNumber
			,PrincipalAmount
			,ServiceFee
			,ServiceFeeRatio
                ,TakeTime
			,TakeStatus
                ,TakeFinishTime
                ,CheckingTime
			,CheckingStatus
			,Remark

		values

			,${id}
			,${platformId}
			,${spvId}
			,${productId}
			,${returnMoneyId}
			,${spvLoanId}
			,#{tradeNumber}
			,#{payPlantformNumber}
			,${principalAmount}
			,${serviceFee}
			,${serviceFeeRatio}
            	,#{takeTime}
			,${takeStatus}
            	,#{takeFinishTime}
            	,#{checkingTime}
			,${checkingStatus}
			,#{remark}


			  select LAST_INSERT_ID() as generatedKey

mapper.delete:
delete from TC_PlatformServiceFee


		and Id=${id}
		and PlatformId=${platformId}
		and SpvId=${spvId}
		and ProductId=${productId}
		and ReturnMoneyId=${returnMoneyId}
		and SpvLoanId=${spvLoanId}
		and TradeNumber=#{tradeNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and PrincipalAmount=${principalAmount}
		and ServiceFee=${serviceFee}
		and ServiceFeeRatio=${serviceFeeRatio}
		and TakeTime=#{takeTime}
		and TakeStatus=${takeStatus}
		and TakeFinishTime=#{takeFinishTime}
		and CheckingTime=#{checkingTime}
		and CheckingStatus=${checkingStatus}
		and Remark=#{remark}
