mapper.select:
select 
	Id productAmountLock_id,
	ProductId productAmountLock_productId,
	SpvId productAmountLock_spvId,
	MemberId productAmountLock_memberId,
	ProductName productAmountLock_productName,
	ProductCode productAmountLock_productCode,
	TransactionNumber productAmountLock_transactionNumber,
	PayPlantformNumber productAmountLock_payPlantformNumber,
	UmpayProductAccountNo productAmountLock_umpayProductAccountNo,
	LockAmount productAmountLock_lockAmount,
	Shares productAmountLock_shares,
	IntoStatus productAmountLock_intoStatus,
	HandleStatus productAmountLock_handleStatus,
	FinishTime productAmountLock_finishTime,
	SubsTime productAmountLock_subsTime,
	ExpireTime productAmountLock_expireTime,
	Remark productAmountLock_remark
from TC_ProductAmountLock


		and Id=${id}
		and ProductId=${productId}
		and SpvId=${spvId}
		and MemberId=${memberId}
		and ProductName=#{productName}
		and ProductCode=#{productCode}
		and TransactionNumber=#{transactionNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and UmpayProductAccountNo=#{umpayProductAccountNo}
		and LockAmount=${lockAmount}
		and Shares=${shares}
		and IntoStatus=${intoStatus}
		and HandleStatus=${handleStatus}
		and FinishTime=#{finishTime}
		and SubsTime=#{subsTime}
		and ExpireTime=${expireTime}
		and Remark=#{remark}

select count(*) from TC_ProductAmountLock


		and Id=${id}
		and ProductId=${productId}
		and SpvId=${spvId}
		and MemberId=${memberId}
		and ProductName=#{productName}
		and ProductCode=#{productCode}
		and TransactionNumber=#{transactionNumber}
		and PayPlantformNumber=#{payPlantformNumber}
		and UmpayProductAccountNo=#{umpayProductAccountNo}
		and LockAmount=${lockAmount}
		and Shares=${shares}
		and IntoStatus=${intoStatus}
		and HandleStatus=${handleStatus}
		and FinishTime=#{finishTime}
		and SubsTime=#{subsTime}
		and ExpireTime=${expireTime}
		and Remark=#{remark}

mapper.insert:
		insert into TC_ProductAmountLock

			,Id
			,ProductId
			,SpvId
			,MemberId
			,ProductName
			,ProductCode
			,TransactionNumber
			,PayPlantformNumber
			,UmpayProductAccountNo
			,LockAmount
			,Shares
			,IntoStatus
			,HandleStatus
                ,FinishTime
                ,SubsTime
			,ExpireTime
			,Remark

		values

			,${id}
			,${productId}
			,${spvId}
			,${memberId}
			,#{productName}
			,#{productCode}
			,#{transactionNumber}
			,#{payPlantformNumber}
			,#{umpayProductAccountNo}
			,${lockAmount}
			,${shares}
			,${intoStatus}
			,${handleStatus}
            	,#{finishTime}
            	,#{subsTime}
			,${expireTime}
			,#{remark}


			  select LAST_INSERT_ID() as generatedKey

