			,Shares
                ,OrderTime
			,InterestRateDate
			,PaymentDate
			,ExpectedExpirationDate
			,ActualExpirationDate
			,ExpectedProfitRatio
			,ExpectedProfit
			,SpvServiceFee
			,SpvServiceFeeRatio
			,ActualLoanAmount
                ,LoanTime
