mapper.insert:
		insert into UC_Organization

			,Id
			,ParentId
			,ParentName
			,Type
			,Code
			,Name
			,ShortName
			,Phone
			,Fax
			,PostCode
			,Address
			,Description
			,Status
			,TreeLevel
			,TreePath
			,IsChildNode
                ,CreateTime
                ,ModifyTime
			,RoleIds

		values

			,${id}
			,${parentId}
			,#{parentName}
			,${type}
			,#{code}
			,#{name}
			,#{shortName}
			,#{phone}
			,#{fax}
			,#{postCode}
			,#{address}
			,#{description}
			,${status}
			,${treeLevel}
			,#{treePath}
			,${isChildNode}
                ,now()
            	,#{modifyTime}
			,#{roleIds}


			  select LAST_INSERT_ID() as generatedKey

